mapper.insert:
INSERT INTO member (

) VALUES (
, #{loginId}
, #{password}
, #{name}
, #{gender}
, TO_DATE(#{birthday},'YYYY-MM-DD')
, SYSDATE
, NULL
)

mapper.update:
UPDATE member
SET
modified_date = SYSDATE
, name = #{name}
, gender = #{gender}
, birthday = TO_DATE(#{birthday},'YYYY-MM-DD')

    , password = #{password}

WHERE
login_id = #{login_id}
